--Day 6- Sql--Create a stored procedure to show the number of students per department.[use ITI DB]USE ITI;CREATE procedure NUM_STDASBEGIN	SELECT D.Dept_Name,COUNT(S.St_Id)	FROM Department AS D	LEFT OUTER JOIN Student AS S	ON S.Dept_Id=D.Dept_Id	GROUP BY D.Dept_NameEND;EXEC NUM_STD--Create a stored procedure that calculates the area of a circle given its radiusCREATE PROCEDURE CAL_AREA 	@RADUIS FLOAT,	@AREA FLOAT OUTPUTAS	BEGIN		SET @AREA = PI()*@RADUIS*@RADUIS;	END;DECLARE @AREA FLOAT;EXEC CAL_AREA @RADUIS=3.0,@AREA = @AREA OUTPUT;PRINT 'The area of the circle is: ' + CAST(@Area AS NVARCHAR);--Create a stored procedure that calculates the sum of a given range of numbersALTER PROCEDURE CALC_SUM 	@START INT,	@END INT,	@SUM INT OUTPUTASBEGIN	SET @SUM = ((@END)*(@END+1)/2)-((@START)*(@START-1)/2);END;DECLARE @START INT,@END INT,@SUM INT;EXEC CALC_SUM @START=1,@END=10 , @SUM=@SUM OUTPUT;PRINT 'the sum of a given range of numbers is: ' + CAST(@SUM AS NVARCHAR);--Create a trigger to prevent anyone from inserting a new record in the Department tableUSE ITI;CREATE TRIGGER NOT_ALLOW_INSERON Department INSTEAD OF INSERTASSELECT 'can’t insert a new record in that table'INSERT INTO Department (Dept_Id,Dept_Name,Dept_Desc,Dept_Location,Dept_Manager,Manager_hiredate)
